svg.script:
 
		var svgNS = "http://www.w3.org/2000/svg";

		function poly(type,x,y,color){
			if(type == 0) { // wide
				a = x;
				b = y;
				c = x + 1;
				d = y + 1;
				e = x;
				f = y + 2;
				g = x - 1;
				h = y + 1;
			}
			if(type == 1) { // vertical right
				a = x;
				b = y;
				c = x;
				d = y + 2;
				e = x - 1;
				f = y + 3;
				g = x - 1;
				h = y + 1;
			}
			if(type == 2) { // vertical left
				a = x;
				b = y;
				c = x + 1;
				d = y + 1;
				e = x + 1;
				f = y + 3;
				g = x;
				h = y + 2;
			}

      var newP = document.createElementNS(svgNS,"polygon");
			if(color == null)
      	newP.setAttributeNS(null,"fill","none");
			if(color != null)
				newP.setAttributeNS(null,"fill",color);
      newP.setAttributeNS(null,"stroke","#000000");		
      newP.setAttributeNS(null,"stroke-width","2");		
      newP.setAttributeNS(null,"stroke-linecap","round");	
      newP.setAttributeNS(null,"stroke-linejoin","bevel");
			newP.setAttributeNS(null,"points",(a * 43.301270189) + "," + (b * 25) + " " + 
																				(c * 43.301270189) + "," + (d * 25) + " " + 
																				(e * 43.301270189) + "," + (f * 25) + " " + 
																				(g * 43.301270189) + "," + (h * 25)
																				);
      document.getElementById("firstGroup").appendChild(newP);
		}
		function start() {
			poly(0,3,0);
			poly(0,4,1);
			poly(0,2,1);
			poly(0,3,2);
			poly(2,2,3);
			poly(1,4,3);
			poly(2,0,5);
			poly(1,6,5);
			poly(2,0,7);
			poly(1,6,7);
			poly(2,1,8);
			poly(1,5,8);


			poly(0,1,2,"#FF0000");
			poly(2,0,3,"#FF0000");
			poly(1,2,3,"#FF0000");
			poly(0,2,5,"#FF0000");
			poly(2,1,6,"#FF0000");
			poly(1,3,6,"#FF0000");
			poly(0,3,8,"#FF0000");
			poly(2,2,9,"#FF0000");
			poly(1,4,9,"#FF0000");
			poly(0,5,2,"#FF0000");
			poly(2,4,3,"#FF0000");
			poly(1,6,3,"#FF0000");
			poly(0,4,5,"#FF0000");
			poly(2,3,6,"#FF0000");
			poly(1,5,6,"#FF0000");
		}
